# Cell
def load_day_file(dt, folder=DAY_FILE_PATH):
    df_path = "%s/%4d/%02d/%02d.tab.gz"%(folder, dt.year, dt.month, dt.day)
    print(df_path)
    df = pd.read_csv(df_path,
                     names=COLUMNS, sep='\t', compression='gzip',
                     dtype=DTYPES,
                     parse_dates=dates,
                     infer_datetime_format=True)
    return df


